import React, { useState, useEffect } from 'react';
import socketIOClient from 'socket.io-client';

const ENDPOINT = "http://localhost:3000";

function App() {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const [receiver, setReceiver] = useState('');
    const [currUser, setCurrUser] = useState('');
    const [sender, setSender] = useState('');
    const [isCurrentUserSet, setIsCurrentUserSet] = useState(false);

    useEffect(() => {
        const socket = socketIOClient(ENDPOINT);

        if (isCurrentUserSet) {
            socket.on('connect', () => {
                console.log('Connected to server');
                socket.emit('join', currUser);  
            });

            socket.on('new_message', (data) => {
                setMessages((prevMessages) => [...prevMessages, data]);
            });
        }

        return () => {
            if (isCurrentUserSet) {
                socket.disconnect();
            }
        };
    }, [isCurrentUserSet, currUser]);  // Dependency on 'receiver' ensures reconnection if receiver changes

    const handleSetCurrentUser = () => {
        setSender(currUser);
        setIsCurrentUserSet(true);
    };

    const sendMessage = async () => {
        try {
            const response = await fetch(`${ENDPOINT}/messages`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ sender, message, receiver }),
            });
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            if (data.status === 'success') {
                setMessage('');
            }
        } catch (error) {
            console.error('Failed to send message:', error);
        }
    };

    return (
        <div className="App">
            <h1>Messaging App</h1>
            {!isCurrentUserSet ? (
                <div>
                    <input
                        type="text"
                        placeholder="Enter your name"
                        value={currUser}
                        onChange={(e) => setCurrUser(e.target.value)}
                    />
                    <button onClick={handleSetCurrentUser}>Set Current User</button>
                </div>
            ) : (
                <div>
                    <input
                        type="text"
                        placeholder="Sender"
                        value={currUser}
                        readOnly
                    />
                    <input
                        type="text"
                        placeholder="Send to"
                        value={receiver}
                        onChange={(e) => setReceiver(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Message"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                    />
                    <button onClick={sendMessage}>Send</button>
                    <div>
                        <h2>Messages broadcasted for {currUser}:</h2>
                        {messages.map((msg, index) => (
                            <div key={index}>
                                <strong>{msg.sender}:</strong> {msg.content}
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
}

export default App;
